        + "select DDLSS.DateKey DateAllKey, DDLSS.*, MEF.MajorEvent, MEF.HolidayInd from (select DDLS.*, SPF.SpecialPeriod from (select DDL.*, SF.Season from (select DD.*, LOC.UserKey, LOC.Location from DateDim DD,"
